CREATE TABLE homemarket.dbo.Users (
  UserID UNIQUEIDENTIFIER NOT NULL DEFAULT (NEWID()),
  UserName VARCHAR(50) NULL,
  ApplicationID UNIQUEIDENTIFIER NULL,
  IsAnonymous BIT NULL,
  LastActivityDate DATETIME NULL,
  WalletBalance DECIMAL(16, 2) NOT NULL DEFAULT (1000000),
  AvatarPicture VARCHAR(MAX) NOT NULL DEFAULT (''),
  CONSTRAINT PK_Users PRIMARY KEY CLUSTERED (UserID)
) 
GO

CREATE TABLE homemarket.dbo.Categories (
  ID INT IDENTITY,
  Name VARCHAR(MAX) NOT NULL,
  Description VARCHAR(MAX) NULL,
  Picture VARCHAR(MAX) NULL,
  Goods VARCHAR(MAX) NULL,
  isVisible BIT NOT NULL DEFAULT 1,
  CONSTRAINT PK_Categories PRIMARY KEY CLUSTERED (ID)
) 
GO

CREATE TABLE homemarket.dbo.Goods (
  ID INT IDENTITY,
  Name VARCHAR(MAX) NOT NULL,
  Description VARCHAR(MAX) NULL,
  Price DECIMAL(16, 2) NULL DEFAULT (0),
  Picture VARCHAR(MAX) NULL,
  Available INT NULL,
  isVisible BIT NOT NULL DEFAULT (1),
  CategoryID INT NOT NULL,
  CONSTRAINT PK_Goods PRIMARY KEY CLUSTERED (ID),
  CONSTRAINT FK_Goods_Categories_ID FOREIGN KEY (CategoryID) REFERENCES dbo.Categories (ID)
) 
GO

CREATE TABLE homemarket.dbo.Orders (
  ID INT IDENTITY,
  Date DATETIME NULL,
  UserID UNIQUEIDENTIFIER NULL,
  GoodID INT NULL,
  Amount INT NOT NULL DEFAULT (1),
  Price DECIMAL(16, 2) NOT NULL DEFAULT (0),
  CONSTRAINT PK_Orders PRIMARY KEY CLUSTERED (ID),
  CONSTRAINT FK_Orders_Goods_ID FOREIGN KEY (GoodID) REFERENCES dbo.Goods (ID),
  CONSTRAINT FK_Orders_Users_UserID FOREIGN KEY (UserID) REFERENCES dbo.Users (UserID),
)
GO
